mapper.insert:
insert into table_user ( user_id, user_email, user_state, user_password, user_name, user_nick, user_address, user_phone, user_gender, user_birth, user_c_date )
VALUES ( #{userId}, #{userEmail}, '0', #{userPassword}, #{userName}, #{userNick}, #{userAddress}, #{userPhone}, #{userGender}, #{userBirth}, now()
)

mapper.select:
		    SELECT count(*)
            FROM table_user 
            WHERE user_id = #{userId}
			AND  user_state = 0
			;
